import React from 'react'
import "./Register.css"
import BannerReg from "./bannerReg.png"
import { Link } from 'react-router-dom';
import { useState, setState } from 'react';

const [Name, setName] = useState(null);
const [email, setEmail] = useState(null);
const [password,setPassword] = useState(null);
const [confirmPassword,setConfirmPassword] = useState(null);
const [birthDate, setBrithDate] = useState(null);
const [phonenumber, setPhonenumber] = useState(null);

const handleInputChange = (e) => {
  const {id , value} = e.target;
  if(id === "Name"){
      setName(value);
  }
  if(id === "email"){
      setEmail(value);
  }
  if(id === "password"){
      setPassword(value);
  }
  if(id === "confirmPassword"){
      setConfirmPassword(value);
  }
  if(id === "birtDate"){
    setBrithDate(value);
}

}

export default function Signup() {
  return (
    <div className='SignupForm'>
       <div className="SignupRow"> 
       <div className="SignUpTitle"><h2>Registreren</h2></div>
       </div>
       <div className="SignupRow">
            <label className="formLabel" for="Name" >Naam</label>
            <input type="text" name="" id="name" value={Name} onChange = {(e) => handleInputChange(e)} className="SignUpInput" />
       </div>
       <div className="SignupRow">
            <label className="formLabel" for="email">E-mailadres </label>
            <input  type="email" name="" id="email" value={email} onChange = {(e) => handleInputChange(e)} className="SignUpInput"/>
       </div>
       <div className="SignupRow">
            <label className="formLabel" for="password">Wachtwoord </label>
            <input  type="password" name="" id="password" value={password} onChange = {(e) => handleInputChange(e)}  className="SignUpInput"/>
       </div>
       <div className="SignupRow">
            <label className="formLabel" for="confirmPassword">Wachtwoord bevestigen </label>
            <input  type="password" name="" id="confirmPassword" value={confirmPassword} onChange = {(e) => handleInputChange(e)} className="SignUpInput"/>
       </div>
       <div className="SignupRow">
            <label className="formLabel" for="birthDate">Geboortedatum </label>
            <input  type="date" name="" id="birthDate" value={birthDate} onChange = {(e) => handleInputChange(e)} className="SignUpInput"/>
       </div>
       <div className="SignupRow">
            <label className="formLabel" for="phonenumber">Telefoonnummer </label>
            <input  type="tel" name="" id="phonenumber" value={phonenumber} onChange = {(e) => handleInputChange(e)} className="SignUpInput"/>
       </div>
       <button type="submit" class="SignupButton">Registreren</button>

       <div>
     <Link to="/login" >Heb je al een account? Klik hier om in te loggen.</Link>
    </div>
      </div>
  )
}
