import { useContext, useState } from "react"
import React from "react";
import "./Login.scs"
import { createUserWithEmailAndPassword, signInWithEmailAndPassword } from "firebase/auth";
import {auth} from "firebase";

import { initializeApp } from "firebase/app";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";

const firebaseConfig = {
  apiKey: "AIzaSyB7i_jPVXN4MEUCjgbQ0jC8_9VzMffWkMY",
  authDomain: "test-c81be.firebaseapp.com",
  projectId: "test-c81be",
  storageBucket: "test-c81be.appspot.com",
  messagingSenderId: "208870836795",
  appId: "1:208870836795:web:fb80f7fc40a0a36528ea1e"
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);

const Login = () => {
  const [error, setError] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const navigate = useNavigate()

  const {dispatch} = useContext(AuthContext)

  const handleLogin = (e) =>{
    e.preventDefault();

    signInWithEmailAndPassword(auth, email, password)
  .then((userCredential) => {
    // Signed in 
    const user = userCredential.user;
    console.log(user);
    dispatch({type:"LOGIN", payload:user})
    navigate("/")
  })
  .catch((error) => {
    setError(true);
  });
  }
  return (
    <div className="login">
      <form onSubmit={handleLogin}>
        <input type="email" placeholder="email" onChange={e=>setEmail(e.target.value)} />
        <input type="password" placeholder="password" onChange={e=>setPassword(e.target.value)} />
        <button type="submit">login</button>
        { error && <span>wrong email or password!</span>}
      </form>
    </div>
  )
}

export default Login
